DROP TABLE IF EXISTS tags_to_articles;

DROP TABLE IF EXISTS articles;

DROP TABLE IF EXISTS type;

DROP TABLE IF EXISTS tags;

DROP TABLE IF EXISTS images;

CREATE TABLE
    images (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        src VARCHAR(255),
        alt VARCHAR(255)
    ) ENGINE = InnoDB DEFAULT CHARSET = utf8;

CREATE TABLE
    tags (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        label VARCHAR(80)
    ) ENGINE = InnoDB DEFAULT CHARSET = utf8;

CREATE TABLE
    type (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        label VARCHAR(80)
    ) ENGINE = InnoDB DEFAULT CHARSET = utf8;

CREATE TABLE
    articles (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        title VARCHAR(255) NOT NULL,
        original_title VARCHAR(255),
        subtitle LONGTEXT,
        resume LONGTEXT,
        text LONGTEXT,
        type_id INT,
        author VARCHAR(80),
        image_id INT,
        CONSTRAINT image_id_fk FOREIGN KEY (image_id) REFERENCES images(id),
        CONSTRAINT type_id_fk FOREIGN KEY (type_id) REFERENCES type(id)
    ) ENGINE = InnoDB DEFAULT CHARSET = utf8;

CREATE TABLE
    tags_to_articles (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        tags_id INT,
        articles_id INT,
        CONSTRAINT articles_id_fk FOREIGN KEY (articles_id) REFERENCES articles(id),
        CONSTRAINT tags_id_fk FOREIGN KEY (tags_id) REFERENCES tags(id)
    ) ENGINE = InnoDB DEFAULT CHARSET = utf8;

